import React from "react";
import { useSelector } from "react-redux";
import Product from "./Product";
import { useParams } from "react-router-dom";
import { useEffect,useS } from "react";

const Cart = () => {
    const state = useSelector((state))
    const {Id} = useParams();
    const [product,setProduct] = useState([]);


    useEffect(()=>{
        const getProduct = async() =>{
            // setLoading(true);
            const response = await fetch(`https://fakestoreapi.com/products/${Id}`);

            console.log(response,"jlkj")

            setProduct(await response.json());
            // setLoading(false);
        }
        getProduct();
    },[Id]);

    handleButton(()=>{
        // const [product ]
    })

    return (
        <div>
            <div className="col-md-6">
            {/* {
                    console.log(product.title,"vctyuiolmn")
                } */}
                    <img src={product.image} alt={product.title} height = "400px" width = "400px" />
                </div>


                <div className="col-md-6">
                    <h4 className="text-uppercase text-black-50">{product.category}</h4>
                    <h1 className="dispaly-5">{product.title}</h1>
                    <p className="lead fw-bolder">
                        Rating{product.rating && product.rating.rate}
                        <i className="fa fa-star"></i>
                    </p>


                    <h3 className="display-6 fw-bold my-4">
                        ${product.price}
                    </h3>

                    <p className="lead ">{product.description }</p>
                </div>



            <div className="row">
                <div className="col-md-4" >
                    <img src={Product.imgage} alt={Product.title}  height="200px" width="180px"/>
                </div>

                <div className="col-md-4">
                    <h3>{Product.title}</h3>
                    <p className="lead fw-bold">
                        {Product.qty} X ${Product.price} = 
                        ${Product.qty * Product.price}
                    </p>
                    {/* <button className="btn btn-outline-dark me-4" onClick={()=>handleButton(Product)}>
                        <i className="fa fa-minus"></i>
                    </button>
                    <button className="btn btn-outline-dark" onClick={()=>handleButton(Product)}>
                        <i className="fa fa-plus"></i>
                    </button> */}

                </div>
            </div> 
        </div>
    )
}
export default Cart;
