import React,{useEffect, useState} from "react";
import { useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import Skeleton from "react-loading-skeleton";

const Cart = () =>{

    const [product,setProduct] = useState([]);
    const {id} = useParams();
    const state = useSelector((state)=> state.product);
    useEffect(()=>{
        const getProduct = async() =>{
            // setLoading(true);
            const response = await fetch(`https://fakestoreapi.com/products/${id}`);
            // console.log(response,'sai');
            setProduct(await response.json());
            // setLoading(false);
        }
        getProduct();
    },[]);
    const handleButton = ()=>{

    }

    return (
        <div>
            <div className="row">
                <div className="col-md-4">
                    <img src={product.imgage} alt={product.title} height = "200px" width = "180px"/>

                </div>

                <div className="col-md-4">
                    <h3>{product.title}</h3>
                    <p className="lead fw-bold">
                        {product.qty} X $ {product.price}=${product.qty * product.price}

                    </p>
                    <button className="btn btn-outline-dark me-4" onClick={()=>handleButton(product)}>
                        <i className="fa fa-minus"></i>
                    </button>                     
                    <button className="btn btn-outline-dark" onClick={()=>handleButton(product)}>                        
                        <i className="fa fa-plus"></i>
                    </button>

                </div>
            </div>
        </div>
    )
}
export default Cart;

